<template>
  <div class="flex justify-between items-center my-4">
    <div class="flex items-center gap-2">
      <a-button>分类</a-button>
      <a-button>标签</a-button>
      <a-button danger>回收站</a-button>
    </div>
    <div class="flex items-center gap-2">
      <a-input-search
        v-model:value="searchText"
        placeholder="搜索"
        style="width: 225px"
        enter-button
        @search="onSearch"
      />
    </div>
  </div>
  <a-table
    :columns="columns"
    :data-source="postList"
    :row-selection="{
      selectedRowKeys: tableSelectedRowKeys,
      onChange: onSelectChange,
    }"
  >
    <template #headerCell="{ column }">
      <template v-if="column.key === 'title'"> 标题 </template>
    </template>

    <template #bodyCell="{ column, record }">
      <template v-if="column.key === 'title'">
        <div class="flex items-center gap-2">
          <a>{{ record.title }}</a>
          <span
            v-if="!record.is_publish"
            id="title-status"
            class="inline-block w-1.5 h-1.5 rounded-full bg-red-600 animate-pulse"
          ></span>
        </div>
      </template>

      <template v-else-if="column.key === 'author'">
        <span>
          {{ record.author }}
        </span>
      </template>
      <template v-else-if="column.key === 'created_at'">
        <span>
          {{ formatTime(record.created_at) }}
        </span>
      </template>
      <template v-else-if="column.key === 'updated_at'">
        <span>
          {{ formatTime(record.updated_at) }}
        </span>
      </template>
      <template v-else-if="column.key === 'category'">
        <a-tag class="text-sm">{{ record.category }}</a-tag>
      </template>
      <template v-else-if="column.key === 'tags'">
        <a-tag class="text-sm" v-for="tag in record.tags" :key="tag">
          {{ tag }}
        </a-tag>
      </template>
      <template v-else-if="column.key === 'cover'">
        <a-image
          :width="50"
          :height="50"
          :src="record.cover"
          fallback="data:image/png;base64,"
        />
      </template>
      <template v-else-if="column.key === 'is_publish'">
        <span>
          <a-tag v-if="record.is_publish" color="success"> 已发布 </a-tag>
          <a-tag v-else color="red"> 未发布 </a-tag>
        </span>
      </template>
      <template v-else-if="column.key === 'action'">
        <span>
          <a-button
            size="small"
            @click="handleChange(record.is_publish, record.id)"
            >{{ record.is_publish ? "下架" : "发布" }}</a-button
          >
          <a-divider type="vertical" />
          <a-button
            size="small"
            @click="
              $router.push({ name: 'EditArticle', params: { id: record.id } })
            "
            >编辑</a-button
          >
          <a-divider type="vertical" />
          <a-button size="small" danger @click="handleDelete(record.id)"
            >删除</a-button
          >
        </span>
      </template>
    </template>
  </a-table>
</template>
<script lang="ts" setup>
import { computed, onMounted, reactive, ref } from "vue";
import type { PostVO } from "@/types/posts";
import { deletePostSoft, getPostsByPage, updatePostStatus } from "@/api/posts";

import { formatTime } from "@/utils/time";
import { message } from "ant-design-vue";

const searchText = ref("");
// 表格选中的行
const tableSelectedRowKeys = ref<string[]>([]);
const posts = reactive({
  page_no: 1,
  size: 10,
  total_count: 0,
  total_page: 1,
  list: [] as PostVO[],
});

// 计算属性，将posts.list中的每一项添加key属性
const postList = computed(() => {
  return posts.list.map(item => {
    return {
      ...item,
      key: item.id,
    };
  });
});
const onSearch = (value: string) => {
  console.log(value);
};

const onSelectChange = (selectedRowKeys: string[]) => {
  console.log(selectedRowKeys);
  tableSelectedRowKeys.value = selectedRowKeys;
};

onMounted(async () => {
  const res = await getPostsByPage({
    page_no: 1,
    size: 10,
  });
  posts.list = res.data.list;
});

const columns = [
  {
    name: "标题",
    width: 200,
    dataIndex: "title",
    key: "title",
    fixed: "left",
  },
  {
    title: "作者",
    width: 100,
    dataIndex: "author",
    key: "author",
  },
  {
    title: "创建时间",
    width: 200,
    dataIndex: "created_at",
    key: "created_at",
  },
  {
    title: "更新时间",
    width: 200,
    dataIndex: "updated_at",
    key: "updated_at",
  },
  {
    title: "分类",
    width: 100,
    key: "category",
    dataIndex: "category",
  },
  {
    title: "标签",
    width: 200,
    key: "tags",
    dataIndex: "tags",
  },
  {
    title: "封面",
    width: 100,
    key: "cover",
    dataIndex: "cover",
  },
  {
    title: "状态",
    width: 100,
    key: "is_publish",
    dataIndex: "is_publish",
    fixed: "right",
  },
  {
    title: "操作",
    width: 500,
    key: "action",
    dataIndex: "action",
    fixed: "right",
  },
];

const handleChange = async (is_publish: boolean, id: string) => {
  try {
    const res = await updatePostStatus({
      id: id,
      is_publish: !is_publish,
    });
    if (res.code === 200) {
      message.success(is_publish ? "下架成功" : "发布成功");
      posts.list = posts.list.map(item => {
        if (item.id === id) {
          item.is_publish = !is_publish;
        }
        return item;
      });
    } else {
      message.error(res.msg);
    }
  } catch (err: any) {
    message.error(err);
  }
};

const handleDelete = async (id: string) => {
  try {
    const res = await deletePostSoft(id);
    message.success(res.msg);
    posts.list = posts.list.filter(item => item.id !== id);
  } catch (err: any) {
    message.error(err);
  }
};
</script>

<style lang="scss" scoped>
:deep(.anticon.anticon-search) {
  display: inline-flex;
}
#title-status::after {
  content: "";
  display: block;
  width: 10px;
  height: 10px;
  border-radius: 50%;
  margin-left: 10px;
}
</style>
